.controls {
	width:150px;
	position: absolute;
	font-family: 'Abel', sans-serif;
	text-align: center;
	font-size:0.9em;
	color:#4f7594;
	background:#efefef;
	right: 0;
	bottom: 0;
}
.controls span.current {
	font-family: century gothic;
    display: inline-block;
    position: absolute;
	margin-top: 3px;
    width: 50px;
    height: 50%;
    left: 50%;
    top: 50%;
    transform: translate(-50%,-50%);
    vertical-align: middle;
}
.controls a {
	vertical-align: middle;
	width: 60px;
	height: 60px;
	display: block;
	position:relative;
}
.controls a.prev{
	float:left;
}
.controls a.next{
	float:right;
}
.controls::after{
	content:"";
	display:block;
	clear:both;
}
.controls a.prev:before {
	content: "";
    width: 20px;
    height: 1px;
    transform: translateY(36px) rotate(45deg);
    left: 15px;
    background: #4f7594;
    position: absolute;
	-webkit-transition: all 0.3s ease;
    -moz-transition: all 0.3s ease;
    -ms-transition: all 0.3s ease;
    -o-transition: all 0.3s ease;
    transition: all 0.3s ease;
}
.controls a.prev:after {
	content: "";
    width: 20px;
    height: 1px;
    transform: translateY(22px) rotate(-45deg);
    left: 15px;
    background: #4f7594;
    position: absolute;
	-webkit-transition: all 0.3s ease;
    -moz-transition: all 0.3s ease;
    -ms-transition: all 0.3s ease;
    -o-transition: all 0.3s ease;
    transition: all 0.3s ease;
}
.controls a.next:before {
	content: "";
    width: 20px;
    height: 1px;
    transform: translateY(22px) rotate(45deg);
    right: 15px;
    background: #4f7594;
    position: absolute;
	-webkit-transition: all 0.3s ease;
    -moz-transition: all 0.3s ease;
    -ms-transition: all 0.3s ease;
    -o-transition: all 0.3s ease;
    transition: all 0.3s ease;
}
.controls a.next:after {
	content: "";
    width: 20px;
    height: 1px;
    transform: translateY(36px) rotate(-45deg);
    right: 15px;
    background: #4f7594;
    position: absolute;
	-webkit-transition: all 0.3s ease;
    -moz-transition: all 0.3s ease;
    -ms-transition: all 0.3s ease;
    -o-transition: all 0.3s ease;
    transition: all 0.3s ease;
}
.controls .prev:hover,
.controls .next:hover{
}
.controls .prev:hover:before,
.controls .prev:hover:after{
	 transform: translateY(29px) rotate(0deg);
	 left: 0;
	 background:#4f7594;
}
.controls .next:hover:before,
.controls .next:hover:after{
	 transform: translateY(29px) rotate(0deg);
	 right: 0;
	 background:#4f7594;
}

