label {
    color:#fff;
	background:#fff;
	width:100px;
	margin:0 auto !important;
	display:block;
	overflow:hidden;
	-webkit-transition: all 0.4s;
	-moz-transition: all 0.4s;
	-ms-transition: all 0.4s;
	-o-transition: all 0.4s;
	transition: all 0.4s;
}
label p.btn-t {
	font-size:0.9em;
	color:#fff;
}
label p.btn{
  display: inline-block;
  position: relative;
  width: 100px;
  height: 100px;
  margin: 0;
  border-radius: 50%;
  -webkit-transition: all 0.4s;
	-moz-transition: all 0.4s;
	-ms-transition: all 0.4s;
	-o-transition: all 0.4s;
	transition: all 0.4s;
}
label p.btn .plus{
  display: block;
  position: absolute;
  top: 50%;
  left: 50%;
  width: 40px;
  height: 1px;
  margin: -2px 0 0 -20px;
  background: transparent;
  transition: .3s;
}
label p.btn .plus:before, label p.btn .plus:after{
  display: block;
  content: "";
  position: absolute;
  top: 50%;
  left: 0;
  width: 40px;
  height: 1px;
  background: #333;
  transition: .3s;
}
label p.btn .plus:before{
  transform: rotate(-0deg);
  -webkit-transform: rotate(-0deg);
}
label p.btn .plus:after{
  transform: rotate(-90deg);
  -webkit-transform: rotate(-90deg);
}
label p.btn:hover .plus:before, label p.btn:hover .plus:after{
	background:#dba24c;
}
p.close-txt{
	opacity:0;
}
label p.close-btn{
	margin-top:0px;
